from aiogram import typesfrom aiogram.dispatcher import FSMContextfrom buttons.markup import MY_GROUPfrom dispatch import dpfrom states import BeginState, MyGroup@dp.message_handler(lambda message: str(message.text).__eq__(MY_GROUP), state=BeginState.begin)async def my_group_handler(message: types.Message, state: FSMContext):	async with state.proxy() as data:		pass	text = 'Sizning guruhlaringiz'	await MyGroup.begin.set()	await message.bot.send_message(chat_id=message.chat.id, text=text)